<?php
$category   = get_queried_object();
$postsCategory = new WP_Query( array(
	'post_type'      => array( 'any' ),
	'post_status'    => 'publish',
	'cat'            => $category->term_id,
	'posts_per_page' => 3
) )

?>

<div class="category-content mt-5 model-1">
	<?php
	if ( $postsCategory->have_posts() ) {

		while ( $postsCategory->have_posts() ) : $postsCategory->the_post();

			$img_destaque = get_the_post_thumbnail_url( get_the_ID(), 'large' );
			$linha_fina   = get_field( 'intro-do-texto', get_the_ID() );
			$veiculo      = get_the_terms( get_the_ID(), 'categoria_veiculos' );

			$data_admin = get_field( 'data', get_the_ID() );

			if ( empty( $data_admin ) ) {
				$data_post = get_the_date( 'd' ) . ' de ' . get_the_date( "F" ) . ' de ' . get_the_date( 'Y' );
			} else {
				$data_post = date( "d", strtotime( $data_admin ) ) . ' de ' . date_i18n( "F", strtotime( $data_admin ) ) . ' de ' . date( "Y", strtotime( $data_admin ) );
			}
			?>

			<div class="cards mb-md-5 border-bottom border-dark pb-5 position-relative">
				<?php if ( ! empty( $img_destaque ) ) { ?>
					<div class="cards__imagem w-100" style="background: url('<?php echo $img_destaque ?>')"></div>
				<?php } ?>

				<div class="cards__texto">
<!--					<div class="categoria border border-dark text-center d-inline-block py-1 px-4">-->
<!--						<a href="--><?php //echo get_category_link($category->term_id) ?><!--" class="text-uppercase">--><?php //echo $category->name ?><!--</a>-->
<!--					</div>-->

					<div class="titulo py-3 px-0">
						<a href="<?php echo get_permalink( get_the_ID() ) ?>">
							<h3 class="m-0 text-uppercase"><?php the_title() ?></h3>

							<?php if ( ! empty( $linha_fina ) ) {
								echo '<p class="m-0 mt-3">'.$linha_fina.'</p>';
							} ?>
						</a>
					</div>

					<div class="data px-0 d-none d-md-block">
						<?php if ( ! empty( $veiculo ) ) {
							echo '<span class="assinatura text-uppercase">'.$veiculo[0]->name.' -</span>';
						} ?>


						<span class="data_post text-uppercase"><?php echo $data_post;?></span>
					</div>
				</div>
			</div>

		<?php endwhile; // end of the loop.



	} ?>


</div>