<?php
$category   = get_queried_object();
$postsCategory = new WP_Query( array(
	'post_type'      => array( 'any' ),
	'post_status'    => 'publish',
	'cat'            => $category->term_id,
	'posts_per_page' => 6
) )

?>

<div class="category-content mt-5 model-2">
	<div class="row">
		<?php
		if ( $postsCategory->have_posts() ) {

			while ( $postsCategory->have_posts() ) : $postsCategory->the_post();

				$img_destaque = get_the_post_thumbnail_url( get_the_ID(), 'large' );
				$linha_fina   = get_field( 'intro-do-texto', get_the_ID() );
				?>

				<div class="col-12 col-md-6 mb-5">
					<div class="cards ">
						<?php
						if ( ! empty( $img_destaque ) ) { ?>
							<div class="cards__imagem w-100"style="background: url('<?php echo $img_destaque ?>')"></div>
						<?php } ?>


						<div class="cards__texto position-relative">
<!--							<div class="categoria border border-dark text-center d-inline-block py-1 px-4">-->
<!--								<a href="--><?php //echo get_category_link( $category->term_id ) ?><!--" class="text-uppercase">--><?php //echo $category->name ?><!--</a>-->
<!--							</div>-->

							<div class="titulo py-2 px-3 px-md-0">
								<a href="<?php echo get_permalink( get_the_ID() ) ?>">
									<h3 class="m-0 text-uppercase"><?php the_title() ?></h3>

									<?php
									if ( ! empty( $linha_fina ) ) {
										echo '<p class="m-0 mt-2">'.$linha_fina.'</p>';
									}
									?>
								</a>
							</div>
						</div>
					</div>
				</div>

			<?php
			endwhile; // end of the loop.



		}
		?>


	</div>

</div>