<?php
$category   = get_queried_object();

$paged = ( get_query_var( 'paged' ) ) ? get_query_var( 'paged' ) : 1;
$postsCategory = new WP_Query( array(
	'post_type'      => array( 'any' ),
	'post_status'    => 'publish',
	'cat'            => $category->term_id,
	'posts_per_page' => 3,
	'paged' => $paged
) )

?>

<div class="category-content mt-5 model-3">
	<div class="d-flex flex-column card-lista">
		<?php
		if ( $postsCategory->have_posts() ) {

			while ( $postsCategory->have_posts() ) : $postsCategory->the_post();

				$img_destaque = get_the_post_thumbnail_url( get_the_ID(), 'large' );
				$linha_fina   = get_field( 'intro-do-texto', get_the_ID() );
				$ano          = get_field( 'ano', get_the_ID() );
				?>

				<div class="d-flex card-lista__block border-top py-4 py-md-5">
					<div class="titulo flex-md-fill mr-md-4 mr-2">
						<?php if ( ! empty( $ano ) ) {
							echo '<span class="flex-md-fill pr-4 pt-4">' . $ano . '</span>';
						} ?>

						<a href="<?php echo get_permalink( get_the_ID() ) ?>">
							<h3 class="m-0 text-uppercase"><?php the_title() ?></h3>

							<?php if ( ! empty( $linha_fina ) ) {
								echo '<p class="m-0 mt-3 d-none d-md-block">' . $linha_fina . '</p>';
							} ?>
						</a>
					</div>

					<?php if ( ! empty( $img_destaque ) ) { ?>
						<div class="imagem flex-md-fill ml-md-4" style="background: url('<?php echo $img_destaque ?>')"></div>
					<?php } ?>

				</div>
			<?php
			endwhile; // end of the loop.



		}
		?>

	</div>
</div>

