<?php
if ( ! is_user_logged_in() ) {
	echo ':)';
} else {
	get_header();

	if ( is_home() ) {
		get_template_part( 'template_partes/pagina-inicial' );
	} elseif ( is_category() ) {
		get_template_part( 'template_partes/categorias' );
	} elseif ( is_single() ) {
		get_template_part( 'template_partes/single' );
	} else {
		get_template_part( 'template_partes/archive' );
	}
	get_footer();
}

