?>

<div class="container categoria mt-4 mt-md-5">
    <div class="row position-relative">
        <div class="col-12 col-md-8 <?php echo $category->slug === 'internacional' || $category->slug === 'gente' || $category->slug === 'esportes-e-esportistas' ? 'pdd-mobile-0' : '' ?>"> <!--pdd-mobile-0-->
